"main";let request;_d61‍.w("request",[["default",function(v){request=v}]]);

const forecast = (longitude, latitude, callback) => {

    const url = `https://api.darksky.net/forecast/3689980556a03b0b346815ebeb9228b6/${latitude},${longitude}?units=si&lang=pt`;
    
    request({ url: url, json: true }, (error, response) => {
        if (error) {
            callback('Error to connect to weather service!');
        } else if (response.body.error) {
            callback('Error to find local weather!! Try another one please!');
        } else {
        const bodyCurrently = response.body.currently;

        console.log(response.body.daily.data[0].summary + ' It is currently ' + bodyCurrently.temperature + ' degress out and there is a ' + bodyCurrently.precipIntensity + '% chance of rain');

        }
    })
}

_d61‍.d(forecast);